import {React, Components} from 'react'


class Add extends Components{

    addIDnote = () =>{
        this.setState({
            id : this.state.id + 1
        })
    }

    getDate = () =>{
        var today = new Date();
        var year = today.getFullYear();
        var month = today.getMonth() + 1;
        var day = today.getDate();
        return `${month}/${day}/${year}`;
    }

    formatDate = (date)=> {
        const year = date.getFullYear();
        const month = date.getMonth();
        const day = date.getDate();
        return `${month}/${day}/${year}`;
        console.log("formatDate performed");
    }

    constructor(props){
        super(props);
        this.state = {
            content : "",
            date: "01/01/2021",
            id : 0,
        }
    }

    render(){
        const {content, date, id} = this.state;
        return(
            <div className="col1_note">
                <div className="col1_textNote" id="col1_textNote${i}" > 
                    {this.state.content}
                </div>
                <div className="col1_date" id="col1_date${i}" > 
                    {this.state.date}
                </div>
            </div>
        )
    }
}
