import {React, Component, useEffect, useState} from 'react';
import ReactMarkdown from 'react-markdown';
import Add from './Add';

const Col = ({popup, togglePopup }) =>{
 const getNewID = (notes) =>{
     let res = -1;
     for (let note in notes){
         if (notes[note].id > res){
             res = notes[note.id];
         }
     }
     return res === -1 ? 0 : res + 1;
 }

 const index = (id ) =>{
    let res = -1;
    noteState.notes.forEach((note,i) => {
        if (note.id === id){
            res = id
        }
        return res;
    })
    console.log("Index performed");
}

const toggleList = () => {
    setIsVisible(!isVisible);
}

const Note = ({note, selectedID, selectNote, toggleList}) => {
    const formatTitle = (text) =>{
        const showLine = text.split ("\n")[0];
        if(showLine === ""){
            return "new note";
        }
        return showLine
    }
    console.log("Note performed");
}

const addNote = () => {
    setNoteState({
        ...noteState,
        notes : noteState.notes.concat({
            id : noteState.newID,
            text : "",
            date : new Date(),
        }),
        selectedID : noteState.newID,
        newID : noteState.newID
    })
    setTextarea("");
    console.log("addNote performed");
}


const editNote = () => {
    const getNewNote = () => {
        const result = noteState.notes.slice(0);
        result [index(noteState.selectedID)].text = textarea;
        return result;
    }
    setNoteState({
        ...noteState,
        notes:getNewNote(),
    })
}

const changeNote = (e) =>{
    setTextarea(e.target.value)
}

const deleteNote = () => {
    if (noteState.selectedID < 0){
        return;
    }
    const nextNote = noteState.notes.filter(
        (note) => note.id < noteState.seledtedID
    );
    const tempID = 
        // ((noteState.notes.length === 0) && (nextPossibleNotes.length === 0))?
        (noteState.notes.length === 0) ? null 
        : (nextPossibleNotes.length === 0 ) ? noteState.notes[1].id : nextPossibleNotes[nextPossibleNotes.length -1].id; 
        console.log("deleteNote performed");
}

const selectNote = (id) => {
    setNoteState({
        ...noteState,
        selectedID : id,
    })
    setTextarea(index(id) != -1 ? noteState.notes[index(id)].text : "")
}

 const Col = ({ popup, togglePopup }) => {
    const [isVisible, setIsVisible] = useState(true);
    const [textarea, setTextarea] = useState("");
     const [noteState, setNotestate] = useState( localStorage.getitem("noteData") === null 
         ? { notes: [] , selectedID : null, newID : 0} 
         : { notes : JSON.parse(window.localStorage.getItem("noteData")),
            selectedID : null, 
            newID : getNewID(JSON.parse(window.localStorage.getItem("noteData")))
         }
        )
    useEffect(() => {
        window.localStorage.setItem("noteData", JSON.stringify(noteState.notes));
    })


 }; 


 return (
    // <div className = "all">
    <div className = "col">
        <div className = "col1">
            <div className = "col1_top">
                <img className = "col1_image" id ="image" alt = 'hyerin' src={require('./hyerin.jpg').default}/> 
                <div className = "col1_title"> My notes </div>
                <span className="material-icons col1_deleteBtn btn" id = "delete">delete</span>
            </div>
            <div className = "col1_search">
                <span className="material-icons col1_searchBtn btn" id="search">search</span>
                <input className="col1_inputSearch" type="text" placeholder="Search all notes"/>
            </div>
            <div className="col1_noteList" id="noteList" >
                {noteState.notes.slice(0).reverse().map((note) => {
                    return (
                        <Note key = {note.id} note = {note} selectedID = {noteState.selectedID} selectNote = {selectNote} toggleList = {toggleList} />
                    )
                })}
            </div>
        </div>
        <div className = "col2">
            <div className = "col2_top">
                <span className="material-icons col2_undoBtn" id = "undo" onClick = {toggleList}>arrow_back</span>
                <span className="material-icons col2_addBtn" id="add" onClick = {addNote}>note_add</span>
            </div>
            <div className = 'col2_note'>
                <textarea className="col2_text" id = "text"
                value = {textarea} onChange= {changeNote}> </textarea>
                <ReactMarkdown className = "col2_markdown" id = "markdown" children = {textarea}> </ReactMarkdown>
            </div>  
        </div>  
      </div>
    // </div>
    )



//  }; 

}
export default Col;
