import {React} from 'react'

const Note = ({note, selectedID}) => {

    const formatDate = (date)=> {
        const year = date.getFullYear();
        const month = date.getMonth();
        const day = date.getDate();
        return `${month}/${day}/${year}`;
        console.log("formatDate performed");
    }

    const getDate = () =>{
        var today = new Date();
        var year = today.getFullYear();
        var month = today.getMonth() + 1;
        var day = today.getDate();
        return `${month}/${day}/${year}`;
        console.log("getDate performed");
    }

    // const {content, date, id} = this.state;

    return(
        <div className={(selectedID === note.id)? "col1_note" : "col1_note"}>
            <div className="col1_textNote" id="col1_textNote" > 
                {this.state.content}
            </div>
            <div className="col1_date" id="col1_date" > 
                {this.state.date}
            </div>
        </div>
    )

}

export default Note;
