import {React} from 'react'

const Note = ({note, selectedID, toggleList, selectNote}) => {
    const formatDate = (date)=> {
        date = new Date();
        const year = date.getFullYear();
        const month = date.getMonth();
        const day = date.getDate();
        return `${month}/${day}/${year}`;
        // console.log("formatDate performed");
    }

    const formatTitle = (text) => {
        const firstLine = text.split("\n")[0];
        if (firstLine === ""){
            return "New note";
        }
        return firstLine;
    }

    return(
        <div className={(selectedID === note.id/1 )? "col1_note col1_note.selected" : "col1_note"} 
            // index = {note.id} onClick = { () => {selectNote(note.id); toggleList();}}>
                index = {note.id} onClick =  {()=>selectNote(note.id)}>
            <div className="col1_text"> {formatTitle(note.text)}</div>
            <div className="col1_textNote" id="col1_textNote" > {note.text}</div>
            <div className="col1_date" id="col1_date" > {formatDate(note.date)} </div>
        </div>
    ) 

}

export default Note;
