import './App.css';
// import './index.js';
// import './Add.js';
import {Component, React} from 'react';
import ReactMarkdown from 'react-markdown'

function App() {
//   render (){

const [ popup, setPopup] = useState(
    window.localStorage.getItem("profileData")=== null? {
        name : "", email : "", location : "", image : "./hyerin.jpg"
    }: JSON.parse(window.localStorage.getItem("profileData"))
)

const setPopupState = (value) =>{
    setPopup(value);
    window.localStorage.setItem("profileData", JSON.stringify(value));
}

const preventTogglePopup = (e) =>{
    e.stopPropagation();
}

const [isVisible, setIsVisible]= useState(false);

const togglePopup = () => {
    setIsVisible(!isVisible);
}

    return (
    // <div className = "all">
    <div className = "col">
        <div className = "col1">
            <div className = "col1_top">
                <img className = "col1_image" id ="image" alt = 'hyerin' src={require('./hyerin.jpg').default}/> 
                <div className = "col1_title"> My notes </div>
                <span className="material-icons col1_deleteBtn btn" id = "delete">delete</span>
            </div>
            <div className = "col1_search">
                <span className="material-icons col1_searchBtn btn" id="search">search</span>
                <input className="col1_inputSearch" type="text" placeholder="Search all notes"/>
            </div>
            <div className="col1_noteList" id="noteList" ></div>
        </div>
        <div className = "col2">
            <div className = "col2_top">
                <span className="material-icons col2_undoBtn" id = "undo" onClick = {toggleList}>arrow_back</span>
                <span className="material-icons col2_addBtn" id="add" onClick = {addNote}>note_add</span>
            </div>
            <div className = 'col2_note'>
                <textarea className = "col2_markdown" id = "markdown"> </textarea>
                <textarea className="col2_text" id = "text"> </textarea>
            </div>  
        </div>  
      </div>
    // </div>
    )
  }
// }


export default App;
